import React, { useState, useEffect } from 'react';
// Импортируй другие компоненты, если есть (формы, списки и т.д.)

function App() {
  // Состояния для доходов и расходов (массивы объектов, например: { amount: 100, date: '2023-10-01', description: 'Зарплата' })
  const [incomes, setIncomes] = useState([]);
  const [expenses, setExpenses] = useState([]);
  // Другие состояния, если есть (например, для заначки или кошелька)

  // useEffect для ЗАГРУЗКИ данных из localStorage при старте приложения
  useEffect(() => {
    const savedIncomes = localStorage.getItem('incomes');
    const savedExpenses = localStorage.getItem('expenses');

    if (savedIncomes) {
      try {
        setIncomes(JSON.parse(savedIncomes));
      } catch (error) {
        console.error('Ошибка загрузки доходов:', error);
      }
    }

    if (savedExpenses) {
      try {
        setExpenses(JSON.parse(savedExpenses));
      } catch (error) {
        console.error('Ошибка загрузки расходов:', error);
      }
    }
  }, []); // Пустой массив зависимостей — запускается только при монтировании

  // useEffect для СОХРАНЕНИЯ данных в localStorage при каждом изменении состояний
  useEffect(() => {
    localStorage.setItem('incomes', JSON.stringify(incomes));
    localStorage.setItem('expenses', JSON.stringify(expenses));
  }, [incomes, expenses]); // Зависимости — состояния, которые отслеживаем

  // Функции для добавления доходов/расходов (пример)
  const addIncome = (newIncome) => {
    setIncomes([...incomes, newIncome]);
  };

  const addExpense = (newExpense) => {
    setExpenses([...expenses, newExpense]);
  };

  // Рендер компонента (формы, списки и т.д.)
  return (
    <div>
      <h1>Бюджетный трекер</h1>
      {/* Здесь добавь формы для ввода данных, используя addIncome и addExpense */}
      {/* Пример: кнопка для теста */}
      <button onClick={() => addIncome({ amount: 500, date: new Date().toISOString().split('T')[0], description: 'Тестовый доход' })}>
        Добавить тестовый доход
      </button>
      <button onClick={() => addExpense({ amount: 100, date: new Date().toISOString().split('T')[0], description: 'Тестовый расход' })}>
        Добавить тестовый расход
      </button>

      {/* Отображение списков */}
      <h2>Доходы:</h2>
      <ul>
        {incomes.map((income, index) => (
          <li key={index}>{income.description}: {income.amount} на {income.date}</li>
        ))}
      </ul>

      <h2>Расходы:</h2>
      <ul>
        {expenses.map((expense, index) => (
          <li key={index}>{expense.description}: {expense.amount} на {expense.date}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
