// App.js
import React, { useState, useEffect } from 'react';
import './App.css';

// Исправлены импорты: ExpenseForm из ExpenseForm, ExpenseList из ExpenseList
import Header from './components/Header/Header';
import ExpenseForm from './components/ExpenseForm/ExpenseForm';  // Форма для добавления расходов
import ExpenseList from './components/ExpenseList/ExpenseList';  // Список расходов
import Summary from './components/Summary/Summary';  // Суммаризация бюджета
import History from './components/History/History';  // История/сброс (предполагаю, что это для просмотра истории и сброса)

function App() {
  const [expenses, setExpenses] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [remainingBudget, setRemainingBudget] = useState(0);
  const [walletUsage, setWalletUsage] = useState(0);
  const [darkMode, setDarkMode] = useState(false);

  // Логика расчётов: пересчитываем при изменении expenses или totalIncome
  useEffect(() => {
    const expSum = expenses.reduce((sum, exp) => sum + parseFloat(exp.amount), 0);
    setTotalExpenses(expSum);
    setRemainingBudget(totalIncome - expSum);
    // Защита от деления на ноль: если totalIncome = 0, walletUsage = 0
    setWalletUsage(totalIncome > 0 ? (expSum / totalIncome) * 100 : 0);
  }, [expenses, totalIncome]);

  // Функции для управления расходами
  const addExpense = (expense) => setExpenses([...expenses, expense]);
  const deleteExpense = (id) => setExpenses(expenses.filter(exp => exp.id !== id));
  const resetAll = () => {
    setExpenses([]);
    setTotalIncome(0);
    setTotalExpenses(0);
    setRemainingBudget(0);
    setWalletUsage(0);
  };

  return (
    <div className={`app ${darkMode ? 'dark' : ''}`}>
      <Header darkMode={darkMode} toggleDarkMode={() => setDarkMode(!darkMode)} />
      <div className="container">
        {/* Форма добавления расходов */}
        <ExpenseForm addExpense={addExpense} setTotalIncome={setTotalIncome} />
        {/* Список расходов с возможностью удаления */}
        <ExpenseList expenses={expenses} deleteExpense={deleteExpense} />
        {/* Суммаризация бюджета */}
        <Summary 
          totalIncome={totalIncome} 
          totalExpenses={totalExpenses} 
          remainingBudget={remainingBudget} 
          walletUsage={walletUsage} 
        />
        {/* История и сброс */}
        <History expenses={expenses} resetAll={resetAll} />
      </div>
    </div>
  );
}

export default App;
