import React, { useState, useEffect } from 'react';

function App() {
  // Состояния
  const [wallet, setWallet] = useState(0);
  const [stash, setStash] = useState(0);
  const [incomes, setIncomes] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [history, setHistory] = useState([]);
  const [totalRemaining, setTotalRemaining] = useState(0);
  const [calculation, setCalculation] = useState([]);

  // Состояния для форм
  const [incomeName, setIncomeName] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');
  const [incomeDate, setIncomeDate] = useState('');
  const [expenseName, setExpenseName] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expensePriority, setExpensePriority] = useState('Средний'); // Дефолтный приоритет
  const [useFromWallet, setUseFromWallet] = useState('');

  // Загрузка данных из localStorage при монтировании компонента
  useEffect(() => {
    const savedWallet = localStorage.getItem('wallet');
    const savedStash = localStorage.getItem('stash');
    const savedIncomes = localStorage.getItem('incomes');
    const savedExpenses = localStorage.getItem('expenses');
    const savedHistory = localStorage.getItem('history');

    if (savedWallet) setWallet(parseFloat(savedWallet));
    if (savedStash) setStash(parseFloat(savedStash));
    if (savedIncomes) setIncomes(JSON.parse(savedIncomes));
    if (savedExpenses) setExpenses(JSON.parse(savedExpenses));
    if (savedHistory) setHistory(JSON.parse(savedHistory));
  }, []);

  // Сохранение в localStorage при изменении состояний
  useEffect(() => {
    localStorage.setItem('wallet', wallet.toString());
    localStorage.setItem('stash', stash.toString());
    localStorage.setItem('incomes', JSON.stringify(incomes));
    localStorage.setItem('expenses', JSON.stringify(expenses));
    localStorage.setItem('history', JSON.stringify(history));
  }, [wallet, stash, incomes, expenses, history]);

  // Функции
  const handleAddIncome = (e) => {
    e.preventDefault();
    const amount = parseFloat(incomeAmount);
    if (incomeName && incomeDate && !isNaN(amount) && amount > 0) {
      const newIncome = { name: incomeName, amount, date: incomeDate };
      setIncomes([...incomes, newIncome]);
      setHistory([...history, { timestamp: new Date().toISOString(), description: `Добавлен доход: ${incomeName}, ${amount.toFixed(2)} руб., дата: ${incomeDate}` }]);
      setIncomeName('');
      setIncomeAmount('');
      setIncomeDate('');
    } else {
      alert('Пожалуйста, введите корректное название, дату и сумму дохода.');
    }
  };

  const handleAddExpense = (e) => {
    e.preventDefault();
    const amount = parseFloat(expenseAmount);
    if (expenseName && expenseDate && !isNaN(amount) && amount > 0 && expensePriority) {
      const newExpense = { name: expenseName, amount, date: expenseDate, priority: expensePriority };
      setExpenses([...expenses, newExpense]);

      const newHistoryEntry = { 
        timestamp: new Date().toISOString(), 
        description: `Добавлен расход: ${expenseName}, ${amount.toFixed(2)} руб., дата: ${expenseDate}, приоритет: ${expensePriority}` 
      };
      setHistory([...history, newHistoryEntry]);

      setExpenseName('');
      setExpenseAmount('');
      setExpenseDate('');
      setExpensePriority('Средний');
    } else {
      alert('Пожалуйста, введите корректное название, дату, сумму расхода и выберите приоритет.');
    }
  };

  const handleDeleteExpense = (idx) => {
    setExpenses(expenses.filter((_, i) => i !== idx));
    setHistory([...history, { timestamp: new Date().toISOString(), description: `Удалён расход: ${expenses[idx].name}` }]);
  };

  const calculate = () => {
    const walletAmount = parseFloat(useFromWallet) || 0;
    if (walletAmount > wallet || walletAmount < 0) {
      alert('Сумма из кошелька должна быть положительной и не превышать доступное в кошельке.');
      return;
    }

    let remainingIncomes = [...incomes].sort((a, b) => new Date(a.date) - new Date(b.date));
    if (walletAmount > 0) {
      remainingIncomes.push({ date: 'Из кошелька', amount: walletAmount });
    }

    const priorityOrder = { 'Высокий': 1, 'Средний': 2, 'Низкий': 3 };
    let remainingExpenses = [...expenses].sort((a, b) => {
      if (priorityOrder[a.priority] !== priorityOrder[b.priority]) {
        return priorityOrder[a.priority] - priorityOrder[b.priority];
      }
      return new Date(a.date) - new Date(b.date);
    });

    let result = [];

    for (let expense of remainingExpenses) {
      let expenseCovered = false;
      for (let income of remainingIncomes) {
        if (income.amount > 0) {
          const coverAmount = Math.min(income.amount, expense.amount);
          result.push(`${expense.name} (${expense.amount.toFixed(2)} руб., дата: ${expense.date}, приоритет: ${expense.priority}) покрыто доходом от ${income.date} на ${coverAmount.toFixed(2)} руб.`);
          income.amount -= coverAmount;
          expense.amount -= coverAmount;
          if (expense.amount <= 0) {
            expenseCovered = true;
            break;
          }
        }
      }
      if (!expenseCovered && expense.amount > 0) {
        result.push(`${expense.name} (дата: ${expense.date}, приоритет: ${expense.priority}) не полностью покрыто (осталось ${expense.amount.toFixed(2)} руб.)`);
      }
    }

    const totalRemainingAmount = remainingIncomes
      .filter(inc => inc.date !== 'Из кошелька')
      .reduce((sum, inc) => sum + inc.amount, 0);
    setTotalRemaining(totalRemainingAmount);

    setCalculation(result);

    if (walletAmount > 0) {
      setWallet(prev => prev - walletAmount);
      setUseFromWallet('');
    }

    setHistory([...history, { timestamp: new Date().toISOString(), description: `Выполнен расчёт: использован кошелёк ${walletAmount.toFixed(2)} руб., остаток доходов: ${totalRemainingAmount.toFixed(2)} руб.` }]);
  };

  // JSX
  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Финансовый менеджер</h1>

      {/* Кошелёк и заначка */}
      <div>
        <h2>Кошелёк и заначка</h2>
        <p>Кошелёк: {wallet.toFixed(2)} руб.</p>
        <p>Заначка: {stash.toFixed(2)} руб.</p>
        <button onClick={() => { setWallet(prev => prev + stash); setStash(0); setHistory([...history, { timestamp: new Date().toISOString(), description: `Переведено из заначки в кошелёк: ${stash.toFixed(2)} руб.` }]); }}>Перевести из заначки в кошелёк</button>
        <button onClick={() => { const amount = parseFloat(prompt('Сумма для перевода в заначку:')); if (!isNaN(amount) && amount > 0 && amount <= wallet) { setWallet(prev => prev - amount); setStash(prev => prev + amount); setHistory([...history, { timestamp: new Date().toISOString(), description: `Переведено в заначку: ${amount.toFixed(2)} руб.` }]); } else { alert('Некорректная сумма.'); } }}>Перевести в заначку</button>
      </div>

      {/* Доходы */}
      <div>
        <h2>Доходы</h2>
        <form onSubmit={handleAddIncome}>
          <input type="text" value={incomeName} onChange={(e) => setIncomeName(e.target.value)} placeholder="Название дохода" required />
          <input type="date" value={incomeDate} onChange={(e) => setIncomeDate(e.target.value)} required />
          <input type="number" value={incomeAmount} onChange={(e) => setIncomeAmount(e.target.value)} placeholder="Сумма" min="0.01" step="0.01" required />
          <button type="submit">Добавить доход</button>
        </form>
        <ul>
          {incomes.map((inc, idx) => (
            <li key={idx}>{inc.name} ({inc.date}): {inc.amount.toFixed(2)} руб.</li>
          ))}
        </ul>
      </div>

      {/* Расходы */}
      <div>
        <h2>Расходы</h2>
        <form onSubmit={handleAddExpense}>
          <input type="text" value={expenseName} onChange={(e) => setExpenseName(e.target.value)} placeholder="Название расхода" required />
          <input type="date" value={expenseDate} onChange={(e) => setExpenseDate(e.target.value)} required />
          <input type="number" value={expenseAmount} onChange={(e) => setExpenseAmount(e.target.value)} placeholder="Сумма" min="0.01" step="0.01" required />
          <select value={expensePriority} onChange={(e) => setExpensePriority(e.target.value)} required>
            <option value="Высокий">Высокий</option>
            <option value="Средний">Средний</option>
            <option value="Низкий">Низкий</option>
          </select>
          <button type="submit">Добавить расход</button>
        </form>
        <ul>
          {expenses.map((exp, idx) => (
            <li key={idx} style={{ 
              color: exp.priority === 'Высокий' ? 'red' : exp.priority === 'Средний' ? 'orange' : 'green',
              fontWeight: exp.priority === 'Высокий' ? 'bold' : 'normal'
            }}>
              {exp.name} ({exp.date}): {exp.amount.toFixed(2)} руб. [Приоритет: {exp.priority}]
              <button onClick={() => handleDeleteExpense(idx)}>Удалить</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Расчёт */}
      <div>
        <h2>Расчёт</h2>
        <input type="number" value={useFromWallet} onChange={(e) => setUseFromWallet(e.target.value)} placeholder="Сумма из кошелька" min="0" step="0.01" />
        <button onClick={calculate}>Рассчитать</button>
        <ul>
          {calculation.map((item, idx) => <li key={idx}>{item}</li>)}
        </ul>
        <p>Остаток доходов: {totalRemaining.toFixed(2)} руб.</p>
      </div>

      {/* История */}
      <div>
        <h2>История</h2>
        <ul>
          {history.map((entry, idx) => (
            <li key={idx}>{new Date(entry.timestamp).toLocaleString()}: {entry.description}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
