import React from 'react';
import './SavingsWallet.css';

function SavingsWallet({ savings, wallet, useFromWallet, setUseFromWallet, onResetSavings, onResetWallet }) {
  return (
    <div className="savings-wallet-container">
      <div className="savings-section">
        <h2 className="section-title">Заначка</h2>
        <p className="total-amount">Общая сумма: {savings.toFixed(2)} руб.</p>
        <button 
          className="reset-icon" 
          onClick={onResetSavings} 
          aria-label="Сбросить заначку"
          title="Сбросить заначку"  // Подсказка при hover
        >
          ✕
        </button>
      </div>
      <div className="wallet-section">
        <h2 className="section-title">Кошелек</h2>
        <p className="total-amount">Общая сумма: {wallet.toFixed(2)} руб.</p>
        <button 
          className="reset-icon" 
          onClick={onResetWallet} 
          aria-label="Сбросить кошелек"
          title="Сбросить кошелек"  // Подсказка при hover
        >
          ✕
        </button>
        {wallet > 0 && (
          <div className="use-wallet-input">
            <label className="input-label">Использовать из кошелька для расходов (руб.):</label>
            <input 
              type="number" 
              value={useFromWallet} 
              onChange={(e) => setUseFromWallet(e.target.value)} 
              placeholder="Сумма" 
              min="0" 
              step="0.01" 
              max={wallet} 
              className="wallet-input"
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default SavingsWallet;
