import React, { useState } from 'react';
import './ExpenseForm.css';

function ExpenseForm({ addExpense, setTotalIncome }) {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [priority, setPriority] = useState('low');
  const [income, setIncome] = useState(0);  // –õ–æ–∫–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –¥–æ—Ö–æ–¥–∞

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!description || !amount) return;
    const expense = {
      id: Date.now(),
      description,
      amount: parseFloat(amount),
      priority,
    };
    // –ü—Ä–æ–≤–µ—Ä–∫–∞: –µ—Å–ª–∏ addExpense –ø–µ—Ä–µ–¥–∞–Ω –∏ —è–≤–ª—è–µ—Ç—Å—è —Ñ—É–Ω–∫—Ü–∏–µ–π, –≤—ã–∑—ã–≤–∞–µ–º –µ—ë
    if (typeof addExpense === 'function') {
      addExpense(expense);
    }
    setDescription('');
    setAmount('');
  };

  const handleIncomeChange = (e) => {
    const value = parseFloat(e.target.value) || 0;
    setIncome(value);
    // –ü—Ä–æ–≤–µ—Ä–∫–∞: –µ—Å–ª–∏ setTotalIncome –ø–µ—Ä–µ–¥–∞–Ω, –≤—ã–∑—ã–≤–∞–µ–º –µ–≥–æ
    if (typeof setTotalIncome === 'function') {
      setTotalIncome(value);
    }
  };

  return (
    <section className="expense-form">
      <h2>üí∞ –î–æ–±–∞–≤–∏—Ç—å –†–∞—Å—Ö–æ–¥</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>–û–ø–∏—Å–∞–Ω–∏–µ:</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: –ï–¥–∞"
            required
          />
        </div>
        <div className="form-group">
          <label>–°—É–º–º–∞ ($):</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="0.00"
            min="0"
            step="0.01"
            required
          />
        </div>
        <div className="form-group">
          <label>–ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç:</label>
          <select value={priority} onChange={(e) => setPriority(e.target.value)}>
            <option value="low">–ù–∏–∑–∫–∏–π</option>
            <option value="medium">–°—Ä–µ–¥–Ω–∏–π</option>
            <option value="high">–í—ã—Å–æ–∫–∏–π</option>
          </select>
        </div>
        <button type="submit" className="button add">–î–æ–±–∞–≤–∏—Ç—å –†–∞—Å—Ö–æ–¥</button>
      </form>

      <div className="income-section">
        <h3>–û–±—â–∏–π –î–æ—Ö–æ–¥</h3>
        <input
          type="number"
          value={income}
          onChange={handleIncomeChange}
          placeholder="–í–≤–µ–¥–∏—Ç–µ –¥–æ—Ö–æ–¥"
          min="0"
          step="0.01"
        />
      </div>
    </section>
  );
}

export default ExpenseForm;
