import React, { useState } from 'react';
import './ExpenseForm.css';

function ExpenseForm({ addExpense, setTotalIncome }) {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('низкий');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (amount && description) {
      addExpense({ id: Date.now(), amount: parseFloat(amount), description, priority });
      setAmount('');
      setDescription('');
    }
  };

  return (
    <section className="expense-form">
      <h2>➕ Добавить Расход</h2>
      <form onSubmit={handleSubmit} className="form">
        <input className="input" type="number" placeholder="Сумма" value={amount} onChange={(e) => setAmount(e.target.value)} />
        <input className="input" type="text" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} />
        <select className="select" value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="низкий">Низкий</option>
          <option value="средний">Средний</option>
          <option value="высокий">Высокий</option>
        </select>
        <button className="button add" type="submit">Добавить</button>
      </form>
      <div className="income-input">
        <input className="input" type="number" placeholder="Общий доход" onChange={(e) => setTotalIncome(parseFloat(e.target.value) || 0)} />
      </div>
    </section>
  );
}

export default ExpenseForm;
