import React from 'react';

function ExpenseForm({ expenseName, setExpenseName, expenseDate, setExpenseDate, expenseAmount, setExpenseAmount, expensePriority, setExpensePriority, onAddExpense }) {
  return (
    <div>
      <h2>Расходы</h2>
      <form onSubmit={onAddExpense}>
        <input type="text" value={expenseName} onChange={(e) => setExpenseName(e.target.value)} placeholder="Название расхода" required />
        <input type="date" value={expenseDate} onChange={(e) => setExpenseDate(e.target.value)} required />
        <input type="number" value={expenseAmount} onChange={(e) => setExpenseAmount(e.target.value)} placeholder="Сумма расхода" min="0.01" step="0.01" required />
        <select value={expensePriority} onChange={(e) => setExpensePriority(e.target.value)} required>
          <option value="Высокий">Высокий</option>
          <option value="Средний">Средний</option>
          <option value="Низкий">Низкий</option>
        </select>
        <button type="submit">Добавить расход</button>
      </form>
    </div>
  );
}

export default ExpenseForm;
