import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [incomes, setIncomes] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [savings, setSavings] = useState(0);
  const [wallet, setWallet] = useState(0);
  const [calculation, setCalculation] = useState(null);
  const [totalRemaining, setTotalRemaining] = useState(0);
  const [history, setHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  // Доход
  const [incomeDate, setIncomeDate] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');

  // Расход
  const [expenseName, setExpenseName] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expensePriority, setExpensePriority] = useState('низкий'); // Новое состояние для приоритета

  // Использование из кошелька
  const [useFromWallet, setUseFromWallet] = useState('');

  const isLoaded = useRef(false);

  // ...

  // Формы расходов и доходов
  return (
    <div className="App">
      <h2>Доходы</h2>
      <form onSubmit={handleAddIncome}>
        <input
          type="date"
          value={incomeDate}
          onChange={(e) => setIncomeDate(e.target.value)}
          placeholder="Дата дохода"
          required
        />
        <input
          type="number"
          value={incomeAmount}
          onChange={(e) => setIncomeAmount(e.target.value)}
          placeholder="Сумма дохода"
          min="0.01"
          step="0.01"
          required
        />
        <button type="submit">Добавить доход</button>
      </form>

      <h2>Расходы</h2>
      <form onSubmit={handleAddExpense}>
        <input
          type="text"
          value={expenseName}
          onChange={(e) => setExpenseName(e.target.value)}
          placeholder="Название расхода"
          required
        />
        <input
          type="date"
          value={expenseDate}
          onChange={(e) => setExpenseDate(e.target.value)}
          placeholder="Дата расхода"
          required
        />
        <input
          type="number"
          value={expenseAmount}
          onChange={(e) => setExpenseAmount(e.target.value)}
          placeholder="Сумма расхода"
          min="0.01"
          step="0.01"
          required
        />
        <select
          value={expensePriority}
          onChange={(e) => setExpensePriority(e.target.value)}
        >
          <option value="низкий">Низкий</option>
          <option value="средний">Средний</option>
          <option value="высокий">Высокий</option>
        </select>
        <button type="submit">Добавить расход</button>
      </form>

      <ul>
        {expenses.map((exp, idx) => (
          <li key={idx}>
            {exp.name} ({exp.date}, {exp.priority}) : {exp.amount} руб.{" "}
            <button onClick={() => handleDeleteExpense(idx)}>Удалить</button>
          </li>
        ))}
      </ul>

      {/* Остальные элементы UI */}
    </div>
  );
}

export default App;
