import React, { useState, useEffect, useRef } from 'react';
import './App.css'; // Для стилей, если нужно

function App() {
  const [incomes, setIncomes] = useState([]); // Массив доступных доходов: [{ date: '2023-10-01', amount: 6720 }] (90% от округлённой суммы)
  const [expenses, setExpenses] = useState([]); // Массив расходов: [{ name: 'Квартира', amount: 500, date: '2023-10-01' }]
  const [savings, setSavings] = useState(0); // Общая сумма заначки
  const [wallet, setWallet] = useState(0); // Общая сумма кошелька (остаток от округления)
  const [calculation, setCalculation] = useState(null); // Результат расчета
  const [totalRemaining, setTotalRemaining] = useState(0); // Остаток доступных доходов после покрытия расходов
  const [history, setHistory] = useState([]); // История операций: массив строк с описаниями действий
  const [showHistory, setShowHistory] = useState(false); // Флаг для показа/скрытия истории

  // Состояния для формы доходов
  const [incomeDate, setIncomeDate] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');

  // Состояния для формы расходов
  const [expenseName, setExpenseName] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenseDate, setExpenseDate] = useState(''); // Новое состояние для даты расхода

  // Состояние для использования из кошелька
  const [useFromWallet, setUseFromWallet] = useState('');

  // Флаг для предотвращения двойной загрузки и преждевременного сохранения
  const isLoaded = useRef(false);

  // Загрузка данных из localStorage при монтировании компонента (только один раз)
  useEffect(() => {
    if (!isLoaded.current) {
      const timestamp = new Date().toISOString();
      console.log(`[${timestamp}] Начинаем загрузку данных из localStorage`);
      try {
        const savedIncomes = localStorage.getItem('incomes');
        if (savedIncomes) {
          const parsed = JSON.parse(savedIncomes);
          console.log(`[${timestamp}] Загружаем incomes:`, parsed); // Отладка
          setIncomes(parsed);
        } else {
          console.log(`[${timestamp}] Incomes не найдены в localStorage`); // Отладка
        }
        const savedExpenses = localStorage.getItem('expenses');
        if (savedExpenses) {
          const parsed = JSON.parse(savedExpenses);
          console.log(`[${timestamp}] Загружаем expenses:`, parsed); // Отладка
          setExpenses(parsed);
        } else {
          console.log(`[${timestamp}] Expenses не найдены в localStorage`); // Отладка
        }
        const savedSavings = localStorage.getItem('savings');
        if (savedSavings) {
          const parsed = parseFloat(savedSavings) || 0;
          console.log(`[${timestamp}] Загружаем savings:`, parsed); // Отладка
          setSavings(parsed);
        } else {
          console.log(`[${timestamp}] Savings не найдены в localStorage`); // Отладка
        }
        const savedWallet = localStorage.getItem('wallet');
        if (savedWallet) {
          const parsed = parseFloat(savedWallet) || 0;
          console.log(`[${timestamp}] Загружаем wallet:`, parsed); // Отладка
          setWallet(parsed);
        } else {
          console.log(`[${timestamp}] Wallet не найдены в localStorage`); // Отладка
        }
        const savedHistory = localStorage.getItem('history');
        if (savedHistory) {
          const parsed = JSON.parse(savedHistory);
          console.log(`[${timestamp}] Загружаем history:`, parsed); // Отладка
          setHistory(parsed);
        } else {
          console.log(`[${timestamp}] History не найдена в localStorage`); // Отладка
        }
        console.log(`[${timestamp}] Загрузка завершена успешно`);
        isLoaded.current = true; // Устанавливаем флаг после загрузки
      } catch (error) {
        console.error(`[${timestamp}] Ошибка загрузки из localStorage:`, error);
      }
    }
  }, []);

  // Отладка: логируем изменения incomes
  useEffect(() => {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] Incomes updated:`, incomes); // Показывает текущее состояние после любого изменения
  }, [incomes]);

  // Отладка: логируем изменения expenses
  useEffect(() => {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] Expenses updated:`, expenses); // Показывает текущее состояние после любого изменения
  }, [expenses]);

  // Отладка: логируем изменения savings
  useEffect(() => {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] Savings updated:`, savings); // Показывает текущее состояние после любого изменения
  }, [savings]);

  // Отладка: логируем изменения wallet
  useEffect(() => {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] Wallet updated:`, wallet); // Показывает текущее состояние после любого изменения
  }, [wallet]);

  // Отладка: логируем изменения history
  useEffect(() => {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] History updated:`, history); // Показывает текущее состояние после любого изменения
  }, [history]);

  // Сохранение incomes в localStorage при изменении (только после загрузки)
  useEffect(() => {
    if (isLoaded.current) {
      const timestamp = new Date().toISOString();
      try {
        console.log(`[${timestamp}] Сохраняем incomes:`, incomes); // Отладка
        localStorage.setItem('incomes', JSON.stringify(incomes));
      } catch (error) {
        console.error(`[${timestamp}] Ошибка сохранения incomes:`, error);
      }
    }
  }, [incomes]);

  // Сохранение expenses в localStorage при изменении (только после загрузки)
  useEffect(() => {
    if (isLoaded.current) {
      const timestamp = new Date().toISOString();
      try {
        console.log(`[${timestamp}] Сохраняем expenses:`, expenses); // Отладка
        localStorage.setItem('expenses', JSON.stringify(expenses));
      } catch (error) {
        console.error(`[${timestamp}] Ошибка сохранения expenses:`, error);
      }
    }
  }, [expenses]);

  // Сохранение savings в localStorage при изменении (только после загрузки)
  useEffect(() => {
    if (isLoaded.current) {
      const timestamp = new Date().toISOString();
      try {
        console.log(`[${timestamp}] Сохраняем savings:`, savings); // Отладка
        localStorage.setItem('savings', savings.toString());
      } catch (error) {
        console.error(`[${timestamp}] Ошибка сохранения savings:`, error);
      }
    }
  }, [savings]);

  // Сохранение wallet в localStorage при изменении (только после загрузки)
  useEffect(() => {
    if (isLoaded.current) {
      const timestamp = new Date().toISOString();
      try {
        console.log(`[${timestamp}] Сохраняем wallet:`, wallet); // Отладка
        localStorage.setItem('wallet', wallet.toString());
      } catch (error) {
        console.error(`[${timestamp}] Ошибка сохранения wallet:`, error);
      }
    }
  }, [wallet]);

  // Сохранение history в localStorage при изменении (только после загрузки)
  useEffect(() => {
    if (isLoaded.current) {
      const timestamp = new Date().toISOString();
      try {
        console.log(`[${timestamp}] Сохраняем history:`, history); // Отладка
        localStorage.setItem('history', JSON.stringify(history));
      } catch (error) {
        console.error(`[${timestamp}] Ошибка сохранения history:`, error);
      }
    }
  }, [history]);

  // Добавление дохода через форму (с округлением и распределением; фиксированный 10% в заначку)
  const handleAddIncome = (e) => {
    e.preventDefault(); // Предотвращаем перезагрузку страницы
    const amount = parseFloat(incomeAmount);
    if (incomeDate && !isNaN(amount) && amount > 0) {
      const rounded = Math.floor(amount / 1000) * 1000; // Округление вниз до 1000
      const walletAmount = amount - rounded; // Остаток в кошелек
      const savingsAmount = rounded * 0.1; // Фиксированные 10% в заначку от округлённой суммы
      const availableAmount = rounded - savingsAmount; // Остальное (90%) доступно для расходов
      setSavings(prev => prev + savingsAmount);
      setWallet(prev => prev + walletAmount);
      setIncomes([...incomes, { date: incomeDate, amount: availableAmount }]);
      // Добавляем в историю
      setHistory([...history, `Добавлен доход: ${incomeDate}, ${availableAmount.toFixed(2)} руб. (заначка: +${savingsAmount.toFixed(2)}, кошелек: +${walletAmount.toFixed(2)})`]);
      setIncomeDate(''); // Сбрасываем инпуты
      setIncomeAmount('');
    } else {
      alert('Пожалуйста, введите корректную дату и сумму дохода.');
    }
  };

  // Добавление расхода через форму (теперь с датой)
  const handleAddExpense = (e) => {
    e.preventDefault(); // Предотвращаем перезагрузку страницы
    const amount = parseFloat(expenseAmount);
    if (expenseName && expenseDate && !isNaN(amount) && amount > 0) {
      setExpenses([...expenses, { name: expenseName, amount, date: expenseDate }]);
      // Добавляем в историю
      setHistory([...history, `Добавлен расход: ${expenseName}, ${amount.toFixed(2)} руб., дата: ${expenseDate}`]);
      setExpenseName(''); // Сбрасываем инпуты
      setExpenseAmount('');
      setExpenseDate('');
    } else {
      alert('Пожалуйста, введите корректное название, дату и сумму расхода.');
    }
  };

  // Удаление дохода (удаляем только доступную сумму; заначка и кошелек остаются)
  const handleDeleteIncome = (index) => {
    const deleted = incomes[index];
    setIncomes(incomes.filter((_, i) => i !== index));
    // Добавляем в историю
    setHistory([...history, `Удалён доход: ${deleted.date}, ${deleted.amount.toFixed(2)} руб.`]);
  };

  // Удаление расхода
  const handleDeleteExpense = (index) => {
    const deleted = expenses[index];
    setExpenses(expenses.filter((_, i) => i !== index));
    // Добавляем в историю
    setHistory([...history, `Удалён расход: ${deleted.name}, ${deleted.amount.toFixed(2)} руб., дата: ${deleted.date}`]);
  };

  // Расчет: распределение расходов по доступным доходам (с возможным использованием кошелька; расходы сортируются по дате)
  const calculate = () => {
    const walletAmount = parseFloat(useFromWallet) || 0;
    if (walletAmount > wallet || walletAmount < 0) {
      alert('Сумма из кошелька должна быть положительной и не превышать доступное в кошельке.');
      return;
    }

    let remainingIncomes = [...incomes].sort((a, b) => new Date(a.date) - new Date(b.date)); // Сортировка по дате
    // Добавляем сумму из кошелька как дополнительный "доход" (без даты, обрабатывается последним)
    if (walletAmount > 0) {
      remainingIncomes.push({ date: 'Из кошелька', amount: walletAmount });
    }

    let remainingExpenses = [...expenses].sort((a, b) => new Date(a.date) - new Date(b.date)); // Сортировка расходов по дате
    let result = [];

    for (let expense of remainingExpenses) {
      let expenseCovered = false;
      for (let income of remainingIncomes) {
        if (income.amount > 0) {
          const coverAmount = Math.min(income.amount, expense.amount);
          result.push(`${expense.name} (${expense.amount}, дата: ${expense.date}) покрыто доходом от ${income.date} на ${coverAmount}`);
          income.amount -= coverAmount;
          expense.amount -= coverAmount;
          if (expense.amount <= 0) {
            expenseCovered = true;
            break;
          }
        }
      }
      if (!expenseCovered && expense.amount > 0) {
        result.push(`${expense.name} (дата: ${expense.date}) не полностью покрыто (осталось ${expense.amount})`);
      }
    }

    // Рассчитываем остаток доступных доходов (исключая использованное из кошелька)
    const totalRemainingAmount = remainingIncomes
      .filter(inc => inc.date !== 'Из кошелька') // Исключаем кошелек из остатка
      .reduce((sum, inc) => sum + inc.amount, 0);
    setTotalRemaining(totalRemainingAmount);

    setCalculation(result);

    // Вычитаем использованное из кошелька
    if (walletAmount > 0) {
      setWallet(prev => prev - walletAmount);
      setUseFromWallet(''); // Сбрасываем поле после использования
    }

    // Добавляем в историю
    setHistory([...history, `Выполнен расчёт: использован кошелёк ${walletAmount.toFixed(2)} руб., остаток доходов: ${totalRemainingAmount.toFixed(2)} руб.`]);
  };

  return (
    <div className="App">
      <h1>Бюджетное приложение</h1>

      <div>
        <h2>Заначка</h2>
        <p>Общая сумма: {savings.toFixed(2)} руб.</p>
        <p>Примечание: Фиксированные 10% от округлённой суммы дохода (вниз до 1000) идут в заначку.</p>
      </div>

      <div>
        <h2>Кошелек</h2>
        <p>Общая сумма: {wallet.toFixed(2)} руб.</p>
        <p>Примечание: Остаток от округления доходов вниз до 1000. Вы можете взять деньги отсюда для покрытия расходов.</p>
        {wallet > 0 && (
          <div>
            <label>Использовать из кошелька для расходов (руб.):</label>
            <input
              type="number"
              value={useFromWallet}
              onChange={(e) => setUseFromWallet(e.target.value)}
              placeholder="Сумма"
              min="0"
              step="0.01"
              max={wallet}
            />
          </div>
        )}
      </div>

      <div>
        <h2>Доходы (доступные для расходов)</h2>
        <form onSubmit={handleAddIncome}>
          <input
            type="date"
            value={incomeDate}
            onChange={(e) => setIncomeDate(e.target.value)}
            placeholder="Дата дохода"
            required
          />
          <input
            type="number"
            value={incomeAmount}
            onChange={(e) => setIncomeAmount(e.target.value)}
            placeholder="Сумма дохода"
            min="0.01"
            step="0.01"
            required
          />
          <button type="submit">Добавить доход</button>
        </form>
        <p>Примечание: Сумма округляется вниз до ближайших 1000 руб. 10% уйдёт в заначку, остаток от округления — в кошелек, остальное (90%) доступно для расходов.</p>
        <ul>
          {incomes.map((inc, idx) => (
            <li key={idx}>
              {inc.date}: {inc.amount.toFixed(2)} руб. 
              <button onClick={() => handleDeleteIncome(idx)}>Удалить</button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Расходы</h2>
        <form onSubmit={handleAddExpense}>
          <input
            type="text"
            value={expenseName}
            onChange={(e) => setExpenseName(e.target.value)}
            placeholder="Название расхода"
            required
          />
          <input
            type="date"
            value={expenseDate}
            onChange={(e) => setExpenseDate(e.target.value)}
            placeholder="Дата расхода"
            required
          />
          <input
            type="number"
            value={expenseAmount}
            onChange={(e) => setExpenseAmount(e.target.value)}
            placeholder="Сумма расхода"
            min="0.01"
            step="0.01"
            required
          />
          <button type="submit">Добавить расход</button>
        </form>
        <ul>
          {expenses.map((exp, idx) => (
            <li key={idx}>
              {exp.name} ({exp.date}): {exp.amount} руб. 
              <button onClick={() => handleDeleteExpense(idx)}>Удалить</button>
            </li>
          ))}
        </ul>
      </div>

      <button onClick={calculate}>Рассчитать</button> {/* Исправил опечатку */}

      {calculation && (
        <div>
          <h2>Результат</h2>
          <ul>
            {calculation.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Отдельная графа для остатка доходов */}
      {totalRemaining !== null && (
        <div>
          <h2>Остаток доходов</h2>
          <p>После покрытия расходов: {totalRemaining.toFixed(2)} руб.</p>
        </div>
      )}

      {/* Раздел истории */}
      <div>
        <h2>История операций</h2>
        <button onClick={() => setShowHistory(!showHistory)}>
          {showHistory ? 'Скрыть историю' : 'Показать историю'}
        </button>
        {showHistory && (
          <ul>
            {history.length > 0 ? (
              history.map((entry, idx) => (
                <li key={idx}>{entry}</li>
              ))
            ) : (
              <li>История пуста.</li>
            )}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
