import React from 'react';

function SavingsWallet({ savings, wallet, useFromWallet, setUseFromWallet, onResetSavings, onResetWallet }) {
  return (
    <div>
      <h2>Заначка</h2>
      <p>Общая сумма: {savings.toFixed(2)} руб.</p>
      <button onClick={onResetSavings}>Сбросить заначку</button>
      <h2>Кошелек</h2>
      <p>Общая сумма: {wallet.toFixed(2)} руб.</p>
      <button onClick={onResetWallet}>Сбросить кошелек</button>
      {wallet > 0 && (
        <div>
          <label>Использовать из кошелька для расходов (руб.):</label>
          <input type="number" value={useFromWallet} onChange={(e) => setUseFromWallet(e.target.value)} placeholder="Сумма" min="0" step="0.01" max={wallet} />
        </div>
      )}
    </div>
  );
}

export default SavingsWallet;
