import React from 'react';

function IncomeForm({ incomeDate, setIncomeDate, incomeAmount, setIncomeAmount, onAddIncome }) {
  return (
    <div>
      <h2>Доходы (доступные для расходов)</h2>
        <form className="income-form" onSubmit={handleSubmit}>
      <h2>Добавить Доход</h2>
      <label>Сумма:</label>
      <input
        type="number"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        placeholder="Введите сумму"
        required
      />
      <label>Описание:</label>
      <input
        type="text"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Описание дохода"
        required
      />
      <label>Приоритет:</label>
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="низкий">Низкий</option>
        <option value="средний">Средний</option>
        <option value="высокий">Высокий</option>
      </select>
      <button type="submit">Добавить</button>
    </form>
      <p>Примечание: Сумма округляется вниз до ближайших 1000 руб. 10% в заначку, остаток от округления в кошелёк, 90% доступно для расходов.</p>
    </div>
  );
}

export default IncomeForm;
