import React from 'react';

function Calculation({ incomes, expenses, onCalculate, calculation, totalRemaining }) {  // Добавлены incomes и expenses в пропсы
  return (
    <div>
      <button 
        onClick={onCalculate} 
        disabled={expenses.length === 0}  // Кнопка отключена, если нет расходов
      >
        Рассчитать
      </button>
      {calculation && (
        <div>
          <h2>Результат</h2>
          <ul>
            {calculation.map((item, idx) => <li key={idx}>{item}</li>)}
          </ul>
        </div>
      )}
      {totalRemaining !== null && (
        <div>
          <h2>Остаток</h2>
          <p>После покрытия расходов: {totalRemaining.toFixed(2)} руб.</p>
        </div>
      )}
    </div>
  );
}

export default Calculation;
