import React, { useState } from 'react';
import './App.css'; // Для стилей, если нужно

function App() {
  const [incomes, setIncomes] = useState([]); // Массив доходов: [{ date: '2023-10-01', amount: 1000 }]
  const [expenses, setExpenses] = useState([]); // Массив расходов: [{ name: 'Квартира', amount: 500 }]
  const [calculation, setCalculation] = useState(null); // Результат расчета

  // Состояния для формы доходов
  const [incomeDate, setIncomeDate] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');

  // Состояния для формы расходов
  const [expenseName, setExpenseName] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');

  // Добавление дохода через форму
  const handleAddIncome = (e) => {
    e.preventDefault(); // Предотвращаем перезагрузку страницы
    const amount = parseFloat(incomeAmount);
    if (incomeDate && !isNaN(amount) && amount > 0) {
      setIncomes([...incomes, { date: incomeDate, amount }]);
      setIncomeDate(''); // Сбрасываем инпуты
      setIncomeAmount('');
    } else {
      alert('Пожалуйста, введите корректную дату и сумму дохода.');
    }
  };

  // Добавление расхода через форму
  const handleAddExpense = (e) => {
    e.preventDefault(); // Предотвращаем перезагрузку страницы
    const amount = parseFloat(expenseAmount);
    if (expenseName && !isNaN(amount) && amount > 0) {
      setExpenses([...expenses, { name: expenseName, amount }]);
      setExpenseName(''); // Сбрасываем инпуты
      setExpenseAmount('');
    } else {
      alert('Пожалуйста, введите корректное название и сумму расхода.');
    }
  };

  // Удаление дохода
  const handleDeleteIncome = (index) => {
    setIncomes(incomes.filter((_, i) => i !== index));
  };

  // Удаление расхода
  const handleDeleteExpense = (index) => {
    setExpenses(expenses.filter((_, i) => i !== index));
  };

  // Расчет: распределение расходов по доходам (логика без изменений)
  const calculate = () => {
    let remainingIncomes = [...incomes].sort((a, b) => new Date(a.date) - new Date(b.date)); // Сортировка по дате
    let remainingExpenses = [...expenses];
    let result = [];

    for (let expense of remainingExpenses) {
      let expenseCovered = false;
      for (let income of remainingIncomes) {
        if (income.amount > 0) {
          const coverAmount = Math.min(income.amount, expense.amount);
          result.push(`${expense.name} (${expense.amount}) покрыто доходом от ${income.date} на ${coverAmount}`);
          income.amount -= coverAmount;
          expense.amount -= coverAmount;
          if (expense.amount <= 0) {
            expenseCovered = true;
            break;
          }
        }
      }
      if (!expenseCovered && expense.amount > 0) {
        result.push(`${expense.name} не полностью покрыто (осталось ${expense.amount})`);
      }
    }

    setCalculation(result);
  };

  return (
    <div className="App">
      <h1>Бюджетное приложение</h1>

      <div>
        <h2>Доходы</h2>
        <form onSubmit={handleAddIncome}>
          <input
            type="date"
            value={incomeDate}
            onChange={(e) => setIncomeDate(e.target.value)}
            placeholder="Дата дохода"
            required
          />
          <input
            type="number"
            value={incomeAmount}
            onChange={(e) => setIncomeAmount(e.target.value)}
            placeholder="Сумма дохода"
            min="0.01"
            step="0.01"
            required
          />
          <button type="submit">Добавить доход</button>
        </form>
        <ul>
          {incomes.map((inc, idx) => (
            <li key={idx}>
              {inc.date}: {inc.amount} руб. 
              <button onClick={() => handleDeleteIncome(idx)}>Удалить</button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Расходы</h2>
        <form onSubmit={handleAddExpense}>
          <input
            type="text"
            value={expenseName}
            onChange={(e) => setExpenseName(e.target.value)}
            placeholder="Название расхода"
            required
          />
          <input
            type="number"
            value={expenseAmount}
            onChange={(e) => setExpenseAmount(e.target.value)}
            placeholder="Сумма расхода"
            min="0.01"
            step="0.01"
            required
          />
          <button type="submit">Добавить расход</button>
        </form>
        <ul>
          {expenses.map((exp, idx) => (
            <li key={idx}>
              {exp.name}: {exp.amount} руб. 
              <button onClick={() => handleDeleteExpense(idx)}>Удалить</button>
            </li>
          ))}
        </ul>
      </div>

      <button onClick={calculate}>Расчитать</button>

      {calculation && (
        <div>
          <h2>Результат</h2>
          <ul>
            {calculation.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
