import React, { useState } from 'react';

function BudgetApp() {
  // Общие состояния
  const [resources, setResources] = useState(10000);
  const [expenses, setExpenses] = useState([]);
  const [income, setIncome] = useState('');
  const [date] = useState(new Date().toISOString().slice(0, 10));
  const [history, setHistory] = useState([]);

  // Для формы расходов
  const [expenseName, setExpenseName] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expensePriority, setExpensePriority] = useState('');

  // Для формы доходов
  const [incomeAmount, setIncomeAmount] = useState('');

  // Общие функции
  const handleAddIncome = (e) => {
    e.preventDefault();
    const amount = parseFloat(incomeAmount);
    if (!isNaN(amount) && amount > 0) {
      setResources(resources + amount);
      setHistory([...history, { timestamp: new Date().toISOString(), description: `Добавлен доход: ${amount.toFixed(2)} руб.` }]);
      setIncomeAmount('');
    } else {
      alert('Введите корректную сумму дохода.');
    }
  };

  const handleAddExpense = (e) => {
    e.preventDefault();
    const amount = parseFloat(expenseAmount);
    const priority = parseInt(expensePriority);
    if (expenseName && expenseDate && !isNaN(amount) && amount > 0 && priority >= 1) {
      const newExpense = { name: expenseName, amount, date: expenseDate, priority };
      setExpenses([...expenses, newExpense]);
      setHistory([...history, { timestamp: new Date().toISOString(), description: `Добавлен расход: ${expenseName}, ${amount.toFixed(2)} руб., дата: ${expenseDate}, приоритет: ${priority}` }]);
      // Очистка формы
      setExpenseName('');
      setExpenseAmount('');
      setExpenseDate('');
      setExpensePriority('');
    } else {
      alert('Пожалуйста, заполните все поля корректно.');
    }
  };

  const handleCalculate = () => {
    // Сортируем расходы по приоритету (1- самый высокий)
    let remainingExpenses = [...expenses].sort((a, b) => a.priority - b.priority);

    let resourcesLeft = resources;
    let expensesPaidThisCycle = [];

    // Проходим по расходам в порядке приоритета
    for (let expense of remainingExpenses) {
      if (resourcesLeft >= expense.amount) {
        resourcesLeft -= expense.amount;
        expensesPaidThisCycle.push({ ...expense, paid: true });
      } else {
        // Остальные расходы не оплачиваем, дессервируется
        expensesPaidThisCycle.push({ ...expense, paid: false });
      }
    }

    // Обновляем расходы с учетом оплаченных должным образом
    // Можно либо сохранить только оплату, либо обновить статус
    // Для простоты: создадим новые расходы с флагом
    setExpenses(remainingExpenses); // только сортировка, оплаченные остаются в списке

    // Обновляем ресурсы
    setResources(resourcesLeft);

    // Записываем историю
    const paidExpenses = expenses.filter(e => {
      // В выбранной логике, мы можем запомнить, что расходы оплачены
      // Но у нас расходы только с данными, нужен флаг. Для простоты — оплачиваем все с высоким приоритетом
      // Но лучше добавить флаг в расходы
      // Пока оставим как есть, для этой версии вам показываю общую идею:

      // Вариант: показать только оплату, предполагая, что оплачены расходы с приоритетом 1
      return false; // пока без конкретной отметки
    });

    setHistory([...history, { timestamp: new Date().toISOString(), description: `Расходы оплачены в порядке приоритета. Остаток ресурсов: ${resourcesLeft.toFixed(2)} руб.` }]);
  };

  // В этом упрощенном примере считаем, что все расходы, оплаченные для отображения, были оплачены в результате
  // Можно улучшить и добавлять флаг оплату при добавлении расходов.

  return (
    <div style={{ padding: '20px' }}>
      <h2>Бюджетный менеджер</h2>

      {/* Текущий ресурсы */}
      <p>Доступные ресурсы: {resources.toFixed(2)} руб.</p>

      {/* Добавление дохода */}
      <form onSubmit={handleAddIncome} style={{ marginBottom: '20px' }}>
        <h3>Добавить доход</h3>
        <input
          type="number"
          placeholder="Сумма дохода"
          value={incomeAmount}
          onChange={(e) => setIncomeAmount(e.target.value)}
          required
        />
        <button type="submit">Добавить доход</button>
      </form>

      {/* Добавление расхода */}
      <form onSubmit={handleAddExpense} style={{ marginBottom: '20px' }}>
        <h3>Добавить расход</h3>
        <input
          type="text"
          placeholder="Название расхода"
          value={expenseName}
          onChange={(e) => setExpenseName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Сумма"
          value={expenseAmount}
          onChange={(e) => setExpenseAmount(e.target.value)}
          required
        />
        <input
          type="date"
          value={expenseDate}
          onChange={(e) => setExpenseDate(e.target.value)}
          required
        />
        <select
          value={expensePriority}
          onChange={(e) => setExpensePriority(e.target.value)}
          required
        >
          <option value="">Приоритет</option>
          <option value="1">Высокий</option>
          <option value="2">Средний</option>
          <option value="3">Низкий</option>
        </select>
        <button type="submit">Добавить расход</button>
      </form>

      {/* Распределение ресурсов */}
      <button onClick={handleCalculate}>Оплатить расходы по приоритету</button>

      {/* Отображение расходов */}
      <h3>Расходы</h3>
      <ul>
        {expenses
          .sort((a, b) => a.priority - b.priority)
          .map((expense, index) => (
            <li key={index}>
              {expense.name} — {expense.amount.toFixed(2)} руб. — приоритет: {['Высокий', 'Средний', 'Низкий'][expense.priority - 1]} — дата: {expense.date}
            </li>
          ))}
      </ul>

      {/* История */}
      <h3>История операций</h3>
      <ul>
        {history.map((entry, index) => (
          <li key={index}>{new Date(entry.timestamp).toLocaleString()}: {entry.description}</li>
        ))}
      </ul>
    </div>
  );
}

export default BudgetApp;
