// Calculation.js
import React from 'react';
import './Calculation.css';

function Calculation({ incomes, expenses, onCalculate, calculation, totalRemaining }) {
  return (
    <div className="calculation">
      <h2>Расчёт Покрытия</h2>
      <button 
        onClick={onCalculate} 
        disabled={expenses.length === 0}
      >
        Рассчитать
      </button>
      {calculation && (
        <div className="result">
          <h3>Результат</h3>
          <ul>
            {calculation.map((item, idx) => <li key={idx}>{item}</li>)}
          </ul>
        </div>
      )}
      {totalRemaining !== null && (
        <div className="remaining">
          <h3>Остаток</h3>
          <p>После покрытия расходов: {totalRemaining.toFixed(2)} руб.</p>
        </div>
      )}
    </div>
  );
}

export default Calculation;
